package task.jnshu.service;import java.util.List;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import task.jnshu.dao.StudentMapper;import task.jnshu.model.Student;/** * 本类完成单个或全体学生信息查询 * 指定条件的学生数量 * Created by Administrator on 7/8/2017. */@Service("studentService")public class StudentServiceImpl implements StudentService{    private static Logger loggerSSI= Logger.getLogger(StudentServiceImpl.class);    @Autowired    private StudentMapper studentMapper;    public Student studentSelect(int id){        loggerSSI.info("测试查询\n");        Student study = new Student();        study = studentMapper.selectByPrimaryKey(id);        loggerSSI.info("学生信息： \n" + study);        return study;    }    public int selectStudentNumber( Integer status){        int i = studentMapper.selectStudentNumber(status);        return i;    }    public List<Student> studentAll(){        List<Student> students = studentMapper.studentAll();        return students;    }}